sys_ui_page.client_script:
var requestBody = ""; 

var client=new XMLHttpRequest();
client.open("get","https://ven02157.service-now.com/api/now/table/sys_user?sysparm_query=sys_id%3D"+g_form.getValue("caller_id")+"&sysparm_fields=phone%2C%20mobile_phone&sysparm_limit=1");

client.setRequestHeader('Accept','application/json');
client.setRequestHeader('Content-Type','application/json');
client.setRequestHeader('X-UserToken',window.userToken);

client.onreadystatechange = function() { 
	if(this.readyState == this.DONE) {
		var phones = JSON.parse(this.response).result[0];
		if(phones.phone != ""){
			document.getElementById("homephone").innerHTML=phones.phone;
			document.getElementById("homenum").style.display="block";
		}
		if(phones.phone != ""){
			document.getElementById("mobilephone").innerHTML=phones.mobile_phone;
			document.getElementById("mobilenum").style.display="block";
		}
	}
}; 
client.send(requestBody);
